    private void btnaltaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnaltaActionPerformed
  productos p2=new productos ();
        p2.setCodProducto(txtcodProducto.getText());
        p2.setNombre(btnNombre.getText ());
        p2.setDescripcion(btnDescripcion.getText ());
        p2.altas();
        txtMensaje.append("Alta de " + txtNombre.getText ()+ System.lineSeparator());
        
        
        
    }//GEN-LAST:event_btnaltaActionPerformed

