    private void btnbuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnbuscarActionPerformed
       txtbuscar.setBackground(java.awt.Color.RED);
        
        productos p2 =new productos ();
        if (p2.buscar(this.txtbuscar.getText())==1){
        txtbuscar.setText("Producto encontrado");
        this.txtcodProducto.setText(p2.getCodProducto());
        this.btnNombre.setText (p2.getNombre());
        this.btnCantidad.setText (p2.getCantidad());
        this.btnDescripcion.setText(p2.getDescripcion());
    }
        else{
                btnbuscar.setText("Producto no encontrado");
                this.btncodProducto.setText ("");
                this.btnNombre.setText ("");
                this.btnCantidad.setText ("");
                this.btnDescripcion.setText ("");
        
    }
    }//GEN-LAST:event_btnbuscarActionPerformed

